import React from 'react';
import { useRef, useState, useEffect } from 'react';
import { faCheck, faTimes, faInfoCircle } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;
const REGISTER_URL = '/register';

const Register = () => {
    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [validName, setValidName] = useState(false);
    const [userFocus, setUserFocus] = useState(false);

    const [pwd, setPwd] = useState('');
    const [validPwd, setValidPwd] = useState(false);
    const [pwdFocus, setPwdFocus] = useState(false);

    const [matchPwd, setMatchPwd] = useState('');
    const [validMatch, setValidMatch] = useState(false);
    const [matchFocus, setMatchFocus] = useState(false);

    const [errMsg, setErrMsg] = useState('');
    const [success, seSuccess] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    }, []);

    useEffect(() => {
        const res = USER_REGEX.test(user);
        console.log(res);
        console.log(user);
        setValidName(res);
    }, [user]);

    useEffect(() => {
        const res = PWD_REGEX.test(pwd);
        console.log(res);
        console.log(pwd);
        setValidPwd(res);
        const match = pwd === matchPwd;
        setValidMatch(match);
    }, [pwd, matchPwd]);

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd, matchPwd]);//when ever user retypes the  username or passwords user has clearly read the error message and made amends

    return (
        <section>
            <p ref={errRef}
                className={errMsg ? "errmsg" : 'offscreen'}
                aria-live='assertive'
            >
                {errMsg}
            </p>
            <h1>
                Register
            </h1>
            <form>
                {/* Username */}
                <label htmlFor="username">
                    Username:
                    <span className={validName ? "valid" : "hide"}>
                        <FontAwesomeIcon icon={faCheck} style={{ color: "#25b601", }} />
                    </span>
                    <span className={validName || user.length > 3 ? "hide" : "invalid"}>
                        <FontAwesomeIcon icon={faTimes} style={{ color: "red", }} />
                    </span>
                </label>
                <br />
                <input
                    type="text"
                    id='username'
                    ref={userRef}
                    autoComplete='off'
                    onChange={(e) => setUser(e.target.value)}
                    required
                    aria-invalid={validName ? 'false' : 'true'}
                    aria-describedby='uidnote'
                    onFocus={() => setUserFocus(true)}
                    onBlur={() => setUserFocus(false)}
                    value={user}
                />
                <p style={{ color: 'red', fontSize: '0.8rem' }} id='uidnote' className={userFocus && user && !validName ? "instructions" : "offscreen"}>
                    <FontAwesomeIcon icon={faInfoCircle} /> <br />
                    4 to 24 characters. <br />
                    Must begin with a letter. <br />
                    Letters, numbers, underscore, hyphens allowed.
                </p>
                {/* Password */}
                <label htmlFor="password">
                    Password:
                    <span className={validName ? "valid" : "hide"}>
                        <FontAwesomeIcon icon={faCheck} style={{ color: "#25b601", }} />
                    </span>
                    <span className={validName || user.length > 3 ? "hide" : "invalid"}>
                        <FontAwesomeIcon icon={faTimes} style={{ color: "red", }} />
                    </span>
                </label>
                <br />
                <input
                    type="text"
                    id='username'
                    ref={userRef}
                    autoComplete='off'
                    onChange={(e) => setUser(e.target.value)}
                    required
                    aria-invalid={validName ? 'false' : 'true'}
                    aria-describedby='uidnote'
                    onFocus={() => setUserFocus(true)}
                    onBlur={() => setUserFocus(false)}
                    value={user}
                />
                <p style={{ color: 'red', fontSize: '0.8rem' }} id='uidnote' className={userFocus && user && !validName ? "instructions" : "offscreen"}>
                    <FontAwesomeIcon icon={faInfoCircle} /> <br />
                    4 to 24 characters. <br />
                    Must begin with a letter. <br />
                    Letters, numbers, underscore, hyphens allowed.
                </p>

            </form>
        </section>
    )
}

export default Register
